import React, { useState } from 'react';
import products from '../../data/products';
import ProductModal from './ProductModal';

function CashierView() {
  const [order, setOrder] = useState([]);
  const [modalProduct, setModalProduct] = useState(null);

  const openModal = (product) => setModalProduct(product);
  const closeModal = () => setModalProduct(null);

  const addToOrder = (productWithExtras) => {
    setOrder((prev) => {
      // Un producto es igual si tiene mismo id, cebolla y salsas
      const key = (item) => `${item.id}-${item.cebolla ? 1 : 0}-${JSON.stringify(item.salsas)}`;
      const existing = prev.find((item) => key(item) === key(productWithExtras));
      if (existing) {
        return prev.map((item) =>
          key(item) === key(productWithExtras)
            ? { ...item, quantity: item.quantity + productWithExtras.quantity }
            : item
        );
      } else {
        return [...prev, productWithExtras];
      }
    });
  };

  const removeFromOrder = (idx) => {
    setOrder((prev) => prev.filter((_, i) => i !== idx));
  };

  const getTotal = () => {
    return order.reduce((sum, item) => sum + item.price * item.quantity, 0);
  };

  return (
    <div style={{ display: 'flex', gap: '2rem', padding: '2rem' }}>
      <div style={{ flex: 1 }}>
        <h2>Men√∫</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem' }}>
          {products.map((product) => (
            <div key={product.id} style={{ border: '1px solid #ccc', borderRadius: 8, padding: 16, minWidth: 150 }}>
              <h4>{product.name}</h4>
              <p>${product.price}</p>
              <button onClick={() => openModal(product)}>Agregar</button>
            </div>
          ))}
        </div>
      </div>
      <div style={{ flex: 1 }}>
        <h2>Orden actual</h2>
        {order.length === 0 ? (
          <p>No hay productos en la orden.</p>
        ) : (
          <ul>
            {order.map((item, idx) => (
              <li key={idx} style={{ marginBottom: 8 }}>
                {item.name} x{item.quantity} - ${item.price * item.quantity}
                {item.cebolla && <span> | Cebolla asada</span>}
                {Object.values(item.salsas).some(v => v > 0) && (
                  <span> | Salsas: {Object.entries(item.salsas).filter(([_, v]) => v > 0).map(([k, v]) => `${k} (${v})`).join(', ')}</span>
                )}
                <button style={{ marginLeft: 8 }} onClick={() => removeFromOrder(idx)}>Quitar</button>
              </li>
            ))}
          </ul>
        )}
        <h3>Total: ${getTotal()}</h3>
        <button disabled={order.length === 0}>Finalizar orden</button>
      </div>
      <ProductModal
        product={modalProduct}
        open={!!modalProduct}
        onClose={closeModal}
        onAdd={addToOrder}
      />
    </div>
  );
}

export default CashierView;
