import React, { useState } from 'react';
import products from '../../data/products';

function CashierView() {
  const [order, setOrder] = useState([]);

  const addToOrder = (product) => {
    setOrder((prev) => {
      const existing = prev.find((item) => item.id === product.id);
      if (existing) {
        return prev.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prev, { ...product, quantity: 1 }];
      }
    });
  };

  const removeFromOrder = (productId) => {
    setOrder((prev) => prev.filter((item) => item.id !== productId));
  };

  const getTotal = () => {
    return order.reduce((sum, item) => sum + item.price * item.quantity, 0);
  };

  return (
    <div style={{ display: 'flex', gap: '2rem', padding: '2rem' }}>
      <div style={{ flex: 1 }}>
        <h2>Men√∫</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem' }}>
          {products.map((product) => (
            <div key={product.id} style={{ border: '1px solid #ccc', borderRadius: 8, padding: 16, minWidth: 150 }}>
              <h4>{product.name}</h4>
              <p>${product.price}</p>
              <button onClick={() => addToOrder(product)}>Agregar</button>
            </div>
          ))}
        </div>
      </div>
      <div style={{ flex: 1 }}>
        <h2>Orden actual</h2>
        {order.length === 0 ? (
          <p>No hay productos en la orden.</p>
        ) : (
          <ul>
            {order.map((item) => (
              <li key={item.id} style={{ marginBottom: 8 }}>
                {item.name} x{item.quantity} - ${item.price * item.quantity}
                <button style={{ marginLeft: 8 }} onClick={() => removeFromOrder(item.id)}>Quitar</button>
              </li>
            ))}
          </ul>
        )}
        <h3>Total: ${getTotal()}</h3>
        <button disabled={order.length === 0}>Finalizar orden</button>
      </div>
    </div>
  );
}

export default CashierView;
